sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	var whenneeded = g_form.getValue("u_when_needed");

	if (isLoading && g_form.isNewRecord()){
		g_form.setValue("u_when_needed", whenneeded);
	}
  if (!g_form.isNewRecord()){
alert("The needed date is changed from " + oldValue + " to " + newValue + "." + " In any case you can change desired date to meet any appropriate need.");
  }
}
